document.addEventListener('DOMContentLoaded',function(){var scrollers=document.querySelectorAll('.scroller');!window.matchMedia('(prefers-reduced-motion: reduce)').matches&&addAnimation();function addAnimation(){for(const scroller of scrollers){scroller.setAttribute('data-animated',!0);var scrollerInner=scroller.querySelector('.scroller__inner'),scrollerContent=[...scrollerInner.children];for(const item of scrollerContent){var duplicatedItem=item.cloneNode(!0);duplicatedItem.setAttribute('aria-hidden',!0);scrollerInner.appendChild(duplicatedItem)}scrollerInner.addEventListener('mouseenter',()=>scrollerInner.setAttribute('paused',''));scrollerInner.addEventListener('mouseleave',()=>scrollerInner.removeAttribute('paused'))}}});
